CREATE OR REPLACE FUNCTION obter_se_prescr_proc_urgente (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_urgente_w		bigint;

BEGIN

select 	count(*)
into STRICT	qt_urgente_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and	ie_urgencia = 'S';

if (qt_urgente_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
