CREATE OR REPLACE FUNCTION obter_se_prescr_reaprazar ( nr_sequencia_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p bigint, dt_inicio_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_inicio_prescr_w	prescr_medica.dt_inicio_prescr%type;
nr_prescricao_w		prescr_medica.nr_prescricao%type;
ie_retorno_w		varchar(1) := 'N';
ie_parametro1_w		varchar(1);


BEGIN

ie_parametro1_w := obter_param_usuario(252, 1, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_parametro1_w);

select 	max(a.dt_inicio_prescr)
into STRICT	dt_inicio_prescr_w
from 	prescr_medica a,
		prescr_material b
where	a.nr_prescricao = b.nr_prescricao
and		b.nr_seq_mat_cpoe = nr_sequencia_p
and		dt_inicio_p between a.dt_inicio_prescr and a.dt_validade_prescr
and		((ie_parametro1_w = 'E' and coalesce(a.dt_liberacao::text, '') = '')
		or (ie_parametro1_w = 'F' and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and coalesce(a.dt_liberacao_farmacia::text, '') = ''));
		
if (coalesce(dt_inicio_prescr_w::text, '') = '') then
	nr_prescricao_w := gpt_obter_prescricao_vigente(nr_sequencia_p, 'M');

	select 	max(dt_inicio_prescr)
	into STRICT	dt_inicio_prescr_w
	from 	prescr_medica
	where	nr_prescricao = nr_prescricao_w
	and		((ie_parametro1_w = 'E' and coalesce(dt_liberacao::text, '') = '')
			or (ie_parametro1_w = 'F' and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') and coalesce(dt_liberacao_farmacia::text, '') = ''));
end if;

if (dt_inicio_p < dt_inicio_prescr_w) then
	ie_retorno_w := 'S';
end if;
	
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
