CREATE OR REPLACE FUNCTION obter_se_prescr_rn (nr_prescricao_p bigint, nr_prescricoes_p text, ie_tipo_item_p text, nr_seq_cpoe_p bigint) RETURNS varchar AS $body$
DECLARE


ie_rn_w			prescr_medica.ie_recem_nato%type := 'N';
nr_prescricao_w		prescr_medica.nr_prescricao%type;
qt_pos_separador_w	bigint;
nr_prescricoes_w		w_adep_t_v.nr_prescricoes%TYPE;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then

	select coalesce(max(ie_recem_nato),'N')
	into STRICT ie_rn_w
	from prescr_medica
	where nr_prescricao = nr_prescricao_p;

elsif (coalesce(nr_seq_cpoe_p,0) > 0) then

	select coalesce(max(ie_recem_nato),'N')
	into STRICT ie_rn_w
	from prescr_medica
	where nr_prescricao = Obter_Nr_Prescr_Seq_Cpoe(ie_tipo_item_p, nr_seq_cpoe_p);

elsif (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') and ((trim(both nr_prescricoes_p) IS NOT NULL AND (trim(both nr_prescricoes_p))::text <> '')) then

	nr_prescricoes_w := trim(both nr_prescricoes_p);
	qt_pos_separador_w := position(',' in nr_prescricoes_w);

	if (qt_pos_separador_w = 0) then

		nr_prescricao_w := (nr_prescricoes_w)::numeric;

		select coalesce(max(ie_recem_nato), 'N')
		into STRICT ie_rn_w
		from prescr_medica
		where nr_prescricao = nr_prescricao_w;

	else
		while( qt_pos_separador_w > 0 ) and (ie_rn_w = 'N') loop

			nr_prescricao_w := (substr(nr_prescricoes_w, 1, qt_pos_separador_w - 1))::numeric;

			select coalesce(max(ie_recem_nato), 'N')
			into STRICT ie_rn_w
			from prescr_medica
			where nr_prescricao = nr_prescricao_w;

			nr_prescricoes_w := substr(nr_prescricoes_w, qt_pos_separador_w + 1, length(nr_prescricoes_w));
			qt_pos_separador_w := position(',' in nr_prescricoes_w);

			if (qt_pos_separador_w = 0) and (ie_rn_w = 'N') then

				nr_prescricao_w := (nr_prescricoes_w)::numeric;

				select coalesce(max(ie_recem_nato), 'N')
				into STRICT ie_rn_w
				from prescr_medica
				where nr_prescricao = nr_prescricao_w;

			end if;

		end loop;

	end if;

else
	ie_rn_w := 'N';
end if;

return ie_rn_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
