CREATE OR REPLACE FUNCTION obter_se_prescr_vazia ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_valor_w	bigint;
ds_retorno_w	varchar(1);


BEGIN
begin
Select 	coalesce(sum(ie_reg),0)
into STRICT	ds_valor_w
from (
	SELECT	1 ie_reg
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	
union

	SELECT	1
	from	prescr_dieta
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	rep_jejum
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	nut_paciente
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	nut_pac
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_solucao
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_material_cid
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_medica_albumina
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_gasoterapia
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_solic_bco_sangue
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_recomendacao
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	hd_prescricao
	where	nr_prescricao = nr_prescricao_p
	
union

	select	1
	from	prescr_leite_deriv
	where	nr_prescricao = nr_prescricao_p) alias2;
exception
when others then
		ds_valor_w := 0;
end;

if (coalesce(ds_valor_w,0) > 0) then
      ds_retorno_w := 'N';
else
      ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
