CREATE OR REPLACE FUNCTION obter_se_prescr_vigente ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_vigente_w		varchar(1);
dt_inicio_prescr_w	timestamp;
dt_validade_prescr_w	timestamp;


BEGIN

Select	dt_inicio_prescr,
	dt_validade_prescr
into STRICT	dt_inicio_prescr_w,
	dt_validade_prescr_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

if (dt_inicio_prescr_w < clock_timestamp()) and (dt_validade_prescr_w > clock_timestamp()) then
	ie_vigente_w := 'S';
else
	ie_vigente_w := 'N';
end if;

return	ie_vigente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
