CREATE OR REPLACE FUNCTION obter_se_presc_amostra (nr_prescricao_p text) RETURNS varchar AS $body$
DECLARE

ie_possui_w varchar(1);

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select  coalesce(max('S'),'N')
	into STRICT 	ie_possui_w
	from   	prescr_proc_peca a,
		tipo_amostra b,
		prescr_procedimento c
	where  	a.nr_seq_tipo 	= b.nr_sequencia
	and	a.nr_prescricao = c.nr_prescricao
	and	a.nr_seq_prescr	= c.NR_sequencia
	and	obter_atendimento_prescr(a.nr_prescricao) = obter_atendimento_prescr(nr_prescricao_p)
	and	b.ie_tipo_exame = 2;
end if;


return	ie_possui_w;

end;
