CREATE OR REPLACE FUNCTION obter_se_presc_tev_profilaxia ( nr_atendimento_p bigint, nr_prescricao_p bigint, dt_avaliacao_tev_p timestamp, qt_horas_ant_tev_p bigint default null, qt_horas_pos_tev_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_seq_tev_min_w	bigint;
dt_avaliacao_tev_w	timestamp;
qt_proc_tev_w		bigint;
qt_mat_tev_w		bigint;
qt_recomend_tev_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_proc_tev_w
from   	prescr_medica a,
		prescr_procedimento b,
		procedimento c
where  	a.nr_prescricao = b.nr_prescricao
and    	a.nr_atendimento = nr_atendimento_p
and		a.nr_prescricao = nr_prescricao_p
and    	b.cd_procedimento = c.cd_procedimento
and    	b.ie_origem_proced = c.ie_origem_proced
and		((a.dt_prescricao  between (dt_avaliacao_tev_p - (1/24 * qt_horas_ant_tev_p))
				  and (dt_avaliacao_tev_p + (1/24 * qt_horas_pos_tev_p))) or
		((qt_horas_ant_tev_p = 0) or (qt_horas_pos_tev_p = 0)))
and    	c.ie_protocolo_tev = 'S';

select 	count(*)
into STRICT	qt_mat_tev_w
from   	prescr_medica a,
		prescr_material b,
		material c
where  	a.nr_prescricao = b.nr_prescricao
and    	b.cd_material = c.cd_material
and    	a.nr_atendimento = nr_atendimento_p
and		a.nr_prescricao = nr_prescricao_p
and		((a.dt_prescricao  between (dt_avaliacao_tev_p - (1/24 * qt_horas_ant_tev_p))
				  and (dt_avaliacao_tev_p + (1/24 * qt_horas_pos_tev_p))) or
		((qt_horas_ant_tev_p = 0) or (qt_horas_pos_tev_p = 0)))
and    	c.ie_protocolo_tev = 'S';

select 	count(*)
into STRICT	qt_recomend_tev_w
from   	prescr_medica a,
		prescr_recomendacao b,
		tipo_recomendacao d
where  	a.nr_prescricao  = b.nr_prescricao
and		a.nr_prescricao = nr_prescricao_p
and		b.cd_recomendacao = d.cd_tipo_recomendacao
and    	a.nr_atendimento = nr_atendimento_p
and		((a.dt_prescricao  between (dt_avaliacao_tev_p - (1/24 * qt_horas_ant_tev_p))
				  and (dt_avaliacao_tev_p + (1/24 * qt_horas_pos_tev_p))) or
		((qt_horas_ant_tev_p = 0) or (qt_horas_pos_tev_p = 0)))
and    	ie_protocolo_tev = 'S';

if (qt_proc_tev_w > 0) or (qt_mat_tev_w > 0) or (qt_recomend_tev_w > 0) then

	ds_retorno_w	:= 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
