CREATE OR REPLACE FUNCTION obter_se_prev_alta (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_prev_alta_w	varchar(50);
nr_seq_w	bigint;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_w
from	atend_previsao_alta
where	nr_atendimento = nr_atendimento_p
and	coalesce(DT_INATIVACAO::text, '') = ''
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

select	max(to_char(dt_previsto_alta,'dd/mm/yyyy hh24:mi:ss'))
into STRICT	dt_prev_alta_w
from	atend_previsao_alta
where	nr_sequencia = nr_seq_w
and	nr_atendimento = nr_atendimento_p
and	coalesce(DT_INATIVACAO::text, '') = ''
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	ie_probabilidade_alta <> 'C'
and	((trunc(dt_previsto_alta) <= trunc(clock_timestamp())) or (trunc(dt_previsto_alta) = trunc(clock_timestamp())) or (trunc(dt_previsto_alta) = trunc(clock_timestamp() + interval '1 days')));

return	dt_prev_alta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
