CREATE OR REPLACE FUNCTION obter_se_pre_medicacao (Nr_prescricao_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pre_medicacao_w 	varchar(1);


BEGIN
	select 	coalesce(max(a.IE_PRE_MEDICACAO), 'N')
	into STRICT 	ie_pre_medicacao_w
	from 	PACIENTE_ATENDIMENTO b,
			PACIENTE_PROTOCOLO_MEDIC a
	where	b.nr_prescricao = nr_prescricao_p
	and 	b.NR_SEQ_PACIENTE = a.NR_SEQ_PACIENTE
	and		a.cd_material = cd_material_p
	and 	coalesce(a.nr_seq_diluicao::text, '') = '';

return	ie_pre_medicacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
