CREATE OR REPLACE FUNCTION obter_se_primeira_coleta ( nr_seq_prescricao_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_primeira_w			varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_primeira_w
from	prescr_proc_etapa
where	nr_seq_prescricao = nr_Seq_prescricao_p
and	nr_prescricao = nr_prescricao_p
and	ie_etapa = 20;

return	ie_primeira_w;

end;
$body$
