CREATE OR REPLACE FUNCTION obter_se_primeira_etapa ( nr_prescricao_p bigint, nr_seq_superior_p bigint, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
nr_etapa_sol_w  bigint;


BEGIN
	select 	nr_etapa_sol
	into STRICT	nr_etapa_sol_w
	from 	prescr_mat_hor
	where 	nr_prescricao = nr_prescricao_p
	and   	nr_seq_material = nr_seq_superior_p
	and   	to_char(dt_horario, 'dd/mm/yyyy hh24:mi:ss') = to_char(dt_horario_p, 'dd/mm/yyyy hh24:mi:ss');
	if (coalesce(nr_etapa_sol_w,0) = 1) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

	return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
