CREATE OR REPLACE FUNCTION obter_se_primeira_mensalidade (nr_seq_mensalidade_p bigint) RETURNS varchar AS $body$
DECLARE


ie_primeira_mensalidade_w	varchar(1);


BEGIN

if (nr_seq_mensalidade_p IS NOT NULL AND nr_seq_mensalidade_p::text <> '') then

	select	coalesce(max(b.ie_primeira_mensalidade),'N')
	into STRICT	ie_primeira_mensalidade_w
	from	pls_lote_mensalidade b,
		pls_mensalidade a
	where	a.nr_seq_lote	= b.nr_sequencia
	and	a.nr_sequencia	= nr_seq_mensalidade_p;
else
	ie_primeira_mensalidade_w := 'N';
end if;

return	ie_primeira_mensalidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
