CREATE OR REPLACE FUNCTION obter_se_primeira_pass_setor_atx ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE
ds_retorno_w	varchar(1) := 'S';
qt_passagens_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_passagens_w
from	atend_paciente_unidade
