CREATE OR REPLACE FUNCTION obter_se_primeira_sae ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, ie_regra_prim_prescr_p text) RETURNS varchar AS $body$
DECLARE


ie_primeira_prescricao_w	varchar(1);


BEGIN
if (ie_regra_prim_prescr_p IS NOT NULL AND ie_regra_prim_prescr_p::text <> '') then

	if (ie_regra_prim_prescr_p = 'A') then

		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_primeira_prescricao_w
		from	pe_prescricao
		where	nr_atendimento 	= nr_atendimento_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	coalesce(dt_inativacao::text, '') = '';

	end if;

end if;

return ie_primeira_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
