CREATE OR REPLACE FUNCTION obter_se_primeiro_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
nr_atendimento_w	bigint;


BEGIN

select	min(nr_atendimento)
into STRICT	nr_atendimento_w
from	atendimento_paciente
where	cd_pessoa_fisica	=	(SELECT	max(cd_pessoa_fisica)
					from	atendimento_paciente
					where	nr_atendimento	=	nr_atendimento_p);

if (nr_atendimento_w = nr_atendimento_p) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
