CREATE OR REPLACE FUNCTION obter_se_primeiro_estagio (nr_seq_estagio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_status_w ESTAGIO_STATUS_ENTREGA.NR_SEQ_STATUS%TYPE;
ie_status_execucao_w STATUS_REGRA_ENTREGA.IE_STATUS_EXECUCAO%TYPE;
result_w varchar(1);


BEGIN
select NR_SEQ_STATUS
into STRICT nr_status_w
from ESTAGIO_STATUS_ENTREGA
where nr_sequencia = nr_seq_estagio_p;

select CASE WHEN count(*)=0 THEN  'N' WHEN count(*)=1 THEN  'S' END
into STRICT result_w
from (SELECT estagio.*, row_number() over (order by nr_seq_ordem, nr_sequencia) as seqnum,
        count(*) over () as last
	from estagio_status_entrega estagio
	where nr_seq_status = nr_status_w
) alias6 where seqnum = CASE WHEN ie_opcao_p='P' THEN  1 WHEN ie_opcao_p='U' THEN  last END 
  and nr_seq_estagio_p = nr_sequencia;

return result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
