CREATE OR REPLACE FUNCTION obter_se_prim_acesso_alerta (nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

					
qt_acesso_dia_w		bigint := 0;	
qt_acesso_dia_atend_w	bigint := 0;					 				


BEGIN
	
if (((coalesce(nr_atendimento_p,0) > 0) or (coalesce(cd_pessoa_fisica_p,'XPTO') <> 'XPTO')) and (coalesce(nm_usuario_p,'XPTO') <> 'XPTO')) then
	if (coalesce(nr_atendimento_p, 0) > 0) then
		select count(*)
		into STRICT	qt_acesso_dia_atend_w	
		from    pep_acesso 
		where  nr_atendimento = nr_atendimento_p
		and     nm_usuario = nm_usuario_p
		and     ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_atualizacao) = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp());	
	elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select count(*)
		into STRICT	qt_acesso_dia_w	
		from    pep_acesso
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p
		and     nm_usuario = nm_usuario_p
		and     ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_atualizacao) = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp());
	
	end if;

end if;

return	qt_acesso_dia_w + qt_acesso_dia_atend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
