CREATE OR REPLACE FUNCTION obter_se_prim_cid_princ ( nr_atendimento_p bigint, nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:=	'N';
nr_seq_interno_w	bigint;


BEGIN

if (nr_atendimento_p > 0) and (nr_seq_interno_p > 0) then
	begin

	select	min(nr_seq_interno)
	into STRICT	nr_seq_interno_w
	from	diagnostico_doenca
	where  	ie_classificacao_doenca	= 'P'
	and	nr_atendimento		= nr_atendimento_p;

	if (nr_seq_interno_p = nr_seq_interno_w) then
		ie_retorno_w	:= 'S';
	end if;
	end;
end if;

