CREATE OR REPLACE FUNCTION obter_se_prim_dia_util_prescr ( nr_prescricao_p text, dt_plano_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		 	 varchar(255) := 'S';
dt_rep_pt_w			  timestamp;
dt_rep_pt2_w		 	 timestamp;
dt_inicio_prescr_w		 	  prescr_medica.dt_inicio_prescr%type;
nr_horas_validade_w	  	 prescr_medica.nr_horas_validade%type;
nr_dia_util_w			 prescr_material.nr_dia_util%type;


BEGIN

select max(dt_rep_pt),
	max(dt_rep_pt2),
	max(trunc(dt_inicio_prescr)),
	max(nr_horas_validade)
into STRICT	dt_rep_pt_w,
	dt_rep_pt2_w,
	dt_inicio_prescr_w,
	nr_horas_validade_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;


if (nr_horas_validade_w > 24) then
	if (dt_plano_p = dt_rep_pt_w) then
		ds_retorno_w	:= 'S';
	elsif (dt_plano_p = dt_rep_pt2_w AND dt_rep_pt2_w > dt_inicio_prescr_w) then
		ds_retorno_w 	:= 'N';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
