CREATE OR REPLACE FUNCTION obter_se_prim_dieta_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prim_dieta_w		varchar(2) := 'S';

BEGIN

select	coalesce(max('N'),'S')
into STRICT	ie_prim_dieta_w
from	prescr_dieta a,
	prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and	b.nr_atendimento = nr_atendimento_p;

return	ie_prim_dieta_w;

end;
