CREATE OR REPLACE FUNCTION obter_se_prim_prescr_periodo ( nr_atendimento_p bigint, nr_prescricao_p bigint, cd_setor_atendimento_p bigint, cd_material_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE

 
ie_primeira_w			varchar(1) := 'N';
nr_prescricao_w			bigint;


BEGIN 
 
SELECT	MIN(a.nr_prescricao) 
INTO STRICT	nr_prescricao_w 
FROM	material c, 
	prescr_medica a, 
	prescr_material b 
WHERE	a.nr_atendimento = nr_atendimento_p 
AND	a.nr_prescricao = b.nr_prescricao 
AND	b.cd_material = c.cd_material 
AND	a.dt_prescricao BETWEEN dt_inicial_p AND dt_final_p 
AND	c.ie_controle_medico <> 0 
AND	(('0' = cd_setor_atendimento_p) OR (Obter_Unidade_Atendimento(a.nr_atendimento,'A', 'CS') = cd_setor_atendimento_p)) 
AND	b.cd_material = cd_material_p;
 
IF (nr_prescricao_w = nr_prescricao_p) THEN 
	ie_primeira_w := 'S';
END IF;
 
RETURN	ie_primeira_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
