CREATE OR REPLACE FUNCTION obter_se_prim_prot_paciente (nr_seq_paciente_p bigint, nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	paciente_setor.cd_pessoa_fisica%type;
nr_seq_paciente_w		paciente_setor.nr_seq_paciente%type;
ds_retorno_w		varchar(1);


BEGIN

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

	select 	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from 	paciente_setor
	where 	nr_seq_paciente = nr_seq_paciente_p;

	select 	min(nr_seq_paciente)
	into STRICT	nr_seq_paciente_w
	from	paciente_setor
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	ie_status <> 'I';

	select 	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	paciente_setor
	where	nr_seq_paciente_w = nr_seq_paciente_p
	and	qt_obter_se_prim_ciclo_dia(nr_seq_atendimento_p) = 'S';

end if;

return ds_retorno_w;

end;
