CREATE OR REPLACE FUNCTION obter_se_prioridade_perfil (nr_seq_prioridade_p bigint) RETURNS varchar AS $body$
DECLARE


/*
retorna 'S' se prioridade liberada para o perfil ativo
*/
ds_retorno_w varchar(1) := 'N';
cd_perfil_w  integer;
qt_perfil_w bigint := 0;

c01 CURSOR FOR
SELECT cd_perfil
from   prioridade_regra_perfil
where  nr_seq_prioridade = nr_seq_prioridade_p;


BEGIN
if (nr_seq_prioridade_p IS NOT NULL AND nr_seq_prioridade_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		cd_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		qt_perfil_w := qt_perfil_w + 1;
		if (obter_perfil_ativo = cd_perfil_w) then
			ds_retorno_w := 'S';
		end if;

		end;
	end loop;
	close c01;
end if;

if (qt_perfil_w = 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
