CREATE OR REPLACE FUNCTION obter_se_prioridade_senha (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w varchar(1);


BEGIN

	SELECT coalesce(MAX('P'),'')
	INTO STRICT retorno_w
	FROM paciente_senha_fila p,
	     fila_espera_senha b
	WHERE p.nr_sequencia = nr_sequencia_p
	and b.nr_sequencia = p.nr_seq_fila_senha
	AND coalesce(p.nr_prioridade,0) < coalesce(b.nr_prioridade_padrao, b.nr_prioridade);


	if (coalesce(retorno_w::text, '') = '') then
		select	MAX('P')
		into STRICT	retorno_w
		FROM 	paciente_senha_fila p,
				fila_espera_senha b
		WHERE 	p.nr_sequencia = nr_sequencia_p
		and 	b.nr_sequencia = p.nr_seq_fila_senha
		and		b.IE_PREFERENCIAL = 'S';
	end if;


	RETURN retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
