SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_problema_existe ( cd_pessoa_fisica_p text, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_w				varchar(1) := 'N';
nr_seq_cad_problema_w	bigint;
cd_doenca_w				varchar(10);
nr_seq_historico_w		bigint;
cd_ciap_w				varchar(5);



BEGIN


if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select   coalesce(max(nr_seq_cad_problema), 0),
			 coalesce(max(cd_doenca), 'N'),
			 coalesce(max(nr_seq_historico), 0),
			 coalesce(max(cd_ciap), 'N')
	into STRICT   	 nr_seq_cad_problema_w,
		     cd_doenca_w,
		     nr_seq_historico_w,
		     cd_ciap_w
	from     lista_problema_pac
	where    nr_sequencia = nr_sequencia_p;

	if ( nr_seq_cad_problema_w > 0 ) or ( cd_doenca_w <> 'N' ) or ( cd_ciap_w <> 'N' ) or ( nr_seq_historico_w > 0) then

		select 	coalesce(max('S'),'N') ie_existe
		into STRICT    ie_existe_w
		from 	lista_problema_pac a
		where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and		nr_sequencia <> nr_sequencia_p
		and 	coalesce(a.dt_fim::text, '') = ''
		and 	coalesce(a.ie_situacao,'A') = 'A'
		and 	coalesce(a.nr_seq_cad_problema,0) = nr_seq_cad_problema_w
		and 	coalesce(a.cd_doenca, 'N') = cd_doenca_w
		and 	coalesce(a.nr_seq_historico,0) = nr_seq_historico_w
		and 	coalesce(a.cd_ciap, 'N') = cd_ciap_w
		and		coalesce(a.nr_seq_problema_sup::text, '') = ''
		and 	coalesce(a.dt_inativacao::text, '') = '';

	end if;


end if;


return ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
