CREATE OR REPLACE FUNCTION obter_se_problema_paciente (ie_problema_paciente_p text, cd_doenca_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


    ds_retorno_w varchar(1) := 'N';
    count_w      bigint;


BEGIN

    IF (substr(ie_problema_paciente_p, 0, 2) = 'PP') THEN
        /* PP = Problema do Paciente */

        SELECT COUNT(*)
          INTO STRICT count_w
          FROM lista_problema_pac pp
         WHERE pp.nr_sequencia = substr(ie_problema_paciente_p, 3)
           AND (((cd_doenca_p IS NOT NULL AND cd_doenca_p::text <> '') AND cd_doenca_p = pp.cd_doenca) OR ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') AND nr_atendimento_p = pp.nr_atendimento));
        IF (count_w > 0) THEN
            ds_retorno_w := 'S';
        END IF;

    ELSIF (substr(ie_problema_paciente_p, 0, 2) = 'DP') THEN
        /* DP = Diagnostico do Paciente */

    
        IF ((cd_doenca_p IS NOT NULL AND cd_doenca_p::text <> '') AND cd_doenca_p = substr(ie_problema_paciente_p, 3)) THEN
            ds_retorno_w := 'S';
        END IF;

    END IF;

    RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
