CREATE OR REPLACE FUNCTION obter_se_procedencia_setor ( cd_procedencia_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            varchar(5);
qt_procedencia_w        integer;
cd_perfil_w		bigint;


BEGIN

cd_perfil_w := obter_perfil_ativo;

select  count(*)
into STRICT    qt_procedencia_w
from    regra_procedencia_setor
where   cd_procedencia = cd_procedencia_p;

ds_retorno_w := 'S';

if (qt_procedencia_w > 0) then

        select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
        into STRICT    ds_retorno_w
        from    regra_procedencia_setor
        where   cd_procedencia = cd_procedencia_p
        and coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
		and ((coalesce(cd_estabelecimento::text, '') = '') OR (cd_estabelecimento = cd_estabelecimento_p))
		and coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w;

end if;
return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
