CREATE OR REPLACE FUNCTION obter_se_proced_doenca_valido (cd_convenio_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_doenca_p text) RETURNS integer AS $body$
DECLARE


/*	0 - Inválido */

ie_tipo_convenio_w	integer;
ie_retorno_w		integer		:= 1;
qt_cid_obrig_w		smallint	:= 0;
qt_cid_igual_w		smallint	:= 0;


BEGIN

/*
select ie_tipo_convenio
into ie_tipo_convenio_w
from convenio
where cd_convenio = cd_convenio_p;
*/
/* Felipe - 27/02/2007 - OS 48332 - Comentei e mudei o select igual como é feito nas rotinas do SUS
select count(*)
into ie_retorno_w
from sus_proced_doenca
where cd_procedimento = cd_procedimento_p
  and ie_origem_proced = ie_origem_proced_p
  and cd_doenca_cid = cd_doenca_p; */
select	coalesce(count(*),0),
	coalesce(sum(CASE WHEN cd_doenca_cid=cd_doenca_p THEN 1  ELSE 0 END ),0)
into STRICT	qt_cid_obrig_w,
	qt_cid_igual_w
from 	sus_proced_doenca
where 	cd_procedimento		= cd_procedimento_p
and 	ie_origem_proced	= ie_origem_proced;

if (qt_cid_obrig_w > 0) and (qt_cid_igual_w = 0) then
	ie_retorno_w	:= 0;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
