CREATE OR REPLACE FUNCTION obter_se_proced_honorario ( cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE




cd_area_procedimento_w		bigint;
cd_especialidade_w		bigint;
cd_grupo_proc_w			bigint;
qt_regra_w			bigint;
ds_retorno_w			varchar(1) := 'N';


BEGIN

select	cd_area_procedimento,
	cd_especialidade,
	cd_grupo_proc
into STRICT	cd_area_procedimento_w,
	cd_especialidade_w,
	cd_grupo_proc_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

select	coalesce(count(*),0)
into STRICT	qt_regra_w
from	regra_honorario_criterio
where	cd_estabelecimento	= cd_estabelecimento_p
and	((cd_procedimento 	= cd_procedimento_p and ie_origem_proced =ie_origem_proced_p )
	or (coalesce(cd_procedimento::text, '') = '' and coalesce(ie_origem_proced::text, '') = ''))
and (cd_area_procedimento	= cd_area_procedimento_w  or coalesce(cd_area_procedimento::text, '') = '')
and (cd_especialidade	= cd_especialidade_w  or coalesce(cd_especialidade::text, '') = '')
and (cd_grupo_proc		= cd_grupo_proc_w or coalesce(cd_grupo_proc::text, '') = '');

if (qt_regra_w = 0 ) then
    ds_retorno_w := 'N';
else
   ds_retorno_w := 'S';
end if;

return	ds_retorno_w;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
