SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proced_isolamento ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';

BEGIN
if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then

	select	coalesce(max(ie_gera_isolamento),'N')
	into STRICT	ds_retorno_w
	from	prescr_procedimento b,
		proc_interno		a
	where	a.nr_sequencia = b.nr_seq_proc_interno
	and	coalesce(a.ie_gera_isolamento,'N') = 'S'
	and (b.nr_sequencia = nr_sequencia_p or b.nr_seq_proc_cpoe = nr_sequencia_p)
	and	b.nr_prescricao	= nr_prescricao_p
	and	(b.nr_seq_proc_interno IS NOT NULL AND b.nr_seq_proc_interno::text <> '');

	if (ds_retorno_w = 'N') then
		select	coalesce(max(ie_gera_isolamento),'N')
		into STRICT	ds_retorno_w
		from	prescr_procedimento b,
			exame_laboratorio a
		where	a.nr_seq_exame = b.nr_seq_exame
		and	coalesce(a.ie_gera_isolamento,'N') = 'S'
		and	b.nr_sequencia = nr_sequencia_p
		and	b.nr_prescricao	= nr_prescricao_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
