CREATE OR REPLACE FUNCTION obter_se_proced_previsto_sus ( nr_seq_atend_futuro_p bigint) RETURNS varchar AS $body$
DECLARE



cd_procedimento_w	bigint;
ie_retorno_w		varchar(1) := 'N';


BEGIN

if (nr_seq_atend_futuro_p IS NOT NULL AND nr_seq_atend_futuro_p::text <> '') then

	select	max(cd_procedimento)
	into STRICT	cd_procedimento_w
	from	atend_pac_proced_previsto
	where	nr_seq_atend_futuro	= nr_seq_atend_futuro_p
	and	ie_proc_principal	= 'S';

	SELECT 	CASE WHEN max(ie_origem_proced)=7 THEN 'S'  ELSE 'N' END
	into STRICT	ie_retorno_w
	FROM   	procedimento
	WHERE  	cd_procedimento = cd_procedimento_w;
end if;

return	ie_retorno_w;

end;
