CREATE OR REPLACE FUNCTION obter_se_proced_setor_eup ( cd_procedencia_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            varchar(5);
qt_procedencia_w        integer;
cd_perfil_w		bigint;
cd_setor_atendimento_w	bigint;
cd_estabelecimento_w	bigint;
ie_tipo_atendimento_w	bigint;


BEGIN

cd_perfil_w := obter_perfil_ativo;
cd_setor_atendimento_w := coalesce(cd_setor_atendimento_p,0);
cd_estabelecimento_w   := coalesce(cd_estabelecimento_p,0);
ie_tipo_atendimento_w  := coalesce(ie_tipo_atendimento_p,0);

select  count(*)
into STRICT    qt_procedencia_w
from    regra_procedencia_setor
where   cd_procedencia = cd_procedencia_p;

ds_retorno_w := 'S';

if (qt_procedencia_w > 0) then

        select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
        into STRICT    ds_retorno_w
        from    regra_procedencia_setor
        where   cd_procedencia = cd_procedencia_p
        and 	coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w
        and  coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and 	coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w) = ie_tipo_atendimento_w;

end if;
return  ds_retorno_w;

end;
$body$
