CREATE OR REPLACE FUNCTION obter_se_proced_txt_padrao (nr_seq_texto_padrao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN
if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	texto_padrao a,
		procedimento_texto_padrao b,
		procedimento c
	where	a.nr_sequencia = b.nr_seq_texto_padrao
	and	b.nr_seq_texto_padrao = nr_seq_texto_padrao_p
	and	((b.cd_procedimento = c.cd_procedimento AND b.ie_origem_proced = c.ie_origem_proced)
	or (b.cd_tipo_procedimento = c.cd_tipo_procedimento))
	and	c.cd_procedimento = cd_procedimento_p
	and	c.ie_origem_proced = ie_origem_proced_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
