CREATE OR REPLACE FUNCTION obter_se_processo_conflito ( nr_seq_processo_p bigint, nr_seq_area_prep_p bigint, ie_exige_area_p text) RETURNS varchar AS $body$
DECLARE


ie_conflito_w	varchar(1) := 'N';


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then
	begin
	Select	coalesce(max('S'),'N')
	into STRICT	ie_conflito_w
	
	where	exists(
	SELECT	1
	from	prescr_mat_hor
	where	nr_seq_processo		= nr_seq_processo_p
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S'
	and	((nr_seq_area_prep	= nr_seq_area_prep_p) or (ie_exige_area_p = 'N'))
	and	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> ''));
	end;
end if;

return ie_conflito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
