CREATE OR REPLACE FUNCTION obter_se_processo_lav_completo ( nr_conjunto_cont_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_existe_w		smallint;
qt_retirada_receb_w	smallint;
qt_ciclo_w		smallint;
nr_seq_conjunto_w	bigint;


BEGIN

select	coalesce(max(nr_seq_conjunto),0)
into STRICT	nr_seq_conjunto_w
from	cm_conjunto_cont
where	nr_sequencia = nr_conjunto_cont_p;

select	count(*)
into STRICT	qt_existe_w
from	cm_conjunto
where	coalesce(ie_controla_processo_lav,'N') = 'S'
and	nr_sequencia = nr_seq_conjunto_w;

if (qt_existe_w > 0) then

	select	max(a)
	into STRICT	qt_retirada_receb_w
	from (SELECT	count(*) a
		from	cm_expurgo_retirada
		where	nr_conjunto_cont = nr_conjunto_cont_p
		and	(dt_retirada IS NOT NULL AND dt_retirada::text <> '')
		
union

		SELECT	count(*) a
		from	cm_expurgo_receb
		where	nr_conjunto_cont = nr_conjunto_cont_p
		and	(dt_recebimneto IS NOT NULL AND dt_recebimneto::text <> '')) alias6;

	select	count(*)
	into STRICT	qt_ciclo_w
	from	cm_ciclo_lavacao a,
		cm_conjunto_cont b
	where	(a.dt_fim IS NOT NULL AND a.dt_fim::text <> '')
	and	a.nr_sequencia = b.nr_seq_ciclo_lav
	and	b.nr_sequencia = nr_conjunto_cont_p;

	if (qt_retirada_receb_w = 0) or (qt_ciclo_w = 0) then
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
