CREATE OR REPLACE FUNCTION obter_se_processo_lib_perfi (cd_processo_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;
ie_liberado_w	varchar(1);

BEGIN

ie_liberado_w := 'S';

if (coalesce(cd_processo_p,0) > 0) then

	select 	count(*)
	into STRICT	qt_reg_w
	from	regra_liberacao_processo a,
		regra_perfil_recepcao b
	where	a.nr_seq_regra_recep = b.nr_sequencia
	and	b.cd_perfil = obter_perfil_ativo;


	if (qt_reg_w > 0) then

		select 	count(*)
		into STRICT	qt_reg_w
		from	regra_liberacao_processo a,
			regra_perfil_recepcao b
		where	a.cd_processo = cd_processo_p
		and	a.nr_seq_regra_recep = b.nr_sequencia
		and	b.cd_perfil = obter_perfil_ativo;

		if (qt_reg_w = 0) then

			ie_liberado_w := 'N';
		end if;

	end if;

end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
