CREATE OR REPLACE FUNCTION obter_se_processo_ompf_pend ( nr_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ompf_pend_w	varchar(1)	:= 'N';


BEGIN
if (nr_processo_p IS NOT NULL AND nr_processo_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_ompf_pend_w
	from	prescr_mat_hor
	where	nr_seq_processo	= nr_processo_p
	and	coalesce(dt_suspensao::text, '') = ''
	and	(nr_seq_etiqueta IS NOT NULL AND nr_seq_etiqueta::text <> '')
	and	obter_status_ordem_frac(nr_seq_etiqueta) <> 'F'
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

end if;

return ie_ompf_pend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
