CREATE OR REPLACE FUNCTION obter_se_proc_agrupa_mat_ipe ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(15) := 'N';
qt_regra_w			bigint;
cd_area_procedimento_w		regra_proc_agrup_mat_ipe.cd_area_procedimento%type := 0;
cd_especialidade_w		regra_proc_agrup_mat_ipe.cd_especialidade%type := 0;
cd_grupo_proc_w			regra_proc_agrup_mat_ipe.cd_grupo_proc%type := 0;
nr_sequencia_w			regra_proc_agrup_mat_ipe.nr_sequencia%type := 0;

C01 CURSOR FOR
	SELECT	coalesce(nr_sequencia,0)
	from	regra_proc_agrup_mat_ipe
	where	ie_situacao = 'A'
	and	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0)
	and	coalesce(cd_area_procedimento,cd_area_procedimento_w) = cd_area_procedimento_w
	and	coalesce(cd_especialidade,cd_especialidade_w) = cd_especialidade_w
	and	coalesce(cd_grupo_proc,cd_grupo_proc_w) = cd_grupo_proc_w
	and	coalesce(cd_procedimento,coalesce(cd_procedimento_p,0)) = coalesce(cd_procedimento_p,0)
	order by 	coalesce(cd_procedimento,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0),
		coalesce(cd_estabelecimento,0);


BEGIN

begin
select	1
into STRICT	qt_regra_w
from	regra_proc_agrup_mat_ipe
where	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0)
and	ie_situacao = 'A'  LIMIT 1;
exception
when others then
	qt_regra_w	:= 0;
end;

if (qt_regra_w > 0) then
	begin

	begin
	select  coalesce(cd_area_procedimento,0),
		coalesce(cd_especialidade_w,0),
		coalesce(cd_grupo_proc_w,0)
	into STRICT    cd_area_procedimento_w,
		cd_especialidade_w,
		cd_grupo_proc_w
	from    estrutura_procedimento_v
	where   cd_procedimento         = coalesce(cd_procedimento_p,0)
	and     ie_origem_proced        = coalesce(ie_origem_proced_p,0);
	exception
	when others then
		cd_area_procedimento_w	:= 0;
		cd_especialidade_w	:= 0;
		cd_grupo_proc_w		:= 0;
	end;

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_sequencia_w := nr_sequencia_w;
		end;
	end loop;
	close C01;

	if (nr_sequencia_w > 0) then
		begin
		ds_retorno_w := 'S';
		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
