CREATE OR REPLACE FUNCTION obter_se_proc_anestesista (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
ds_retorno_w		varchar(2);

BEGIN

select	count(*)
into STRICT	qt_registro_w
from	procedimento_participante a,
	funcao_medico b
where	a.ie_funcao = b.cd_funcao
and	a.nr_sequencia = nr_sequencia_p
and	b.ie_anestesista = 'S';

if (qt_registro_w = 0) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
