CREATE OR REPLACE FUNCTION obter_se_proc_anest (nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ie_proc_anest_w	varchar(1) := 'N';


BEGIN

if (nr_seq_proc_interno_p > 0) then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_proc_anest_w
	from 	proc_interno
	where 	nr_sequencia = nr_seq_proc_interno_p
	and	coalesce(ie_necessita_anest,'N') = 'S';

elsif (cd_procedimento_p > 0) and (ie_origem_proc_p > 0) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_proc_anest_w
	from 	proc_interno
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proc_p
	and	coalesce(ie_necessita_anest,'N') = 'S';

end if;

return	ie_proc_anest_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
