SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proc_apac_solic ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(1)	:= 'N';
ie_nivel_atencao_w  varchar(1)	:= 'T';


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then


    Select  wheb_assist_pck.get_nivel_atencao_perfil
	into STRICT	ie_nivel_atencao_w
	;

	if ( ie_nivel_atencao_w in ('P','S')) then

		Select  coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	pedido_exame_externo_item a,
				pedido_exame_externo b
		where	a.nr_seq_pedido = b.nr_sequencia
		and		b.nr_sequencia = nr_sequencia_p
		and		Sus_Obter_TipoReg_Proc(a.cd_procedimento,7, 'C', 3) in (6,7);

	end if;

end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
