SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proc_aprov_urg ( nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_urgente_w		varchar(01);
ie_documento_w		varchar(01);


BEGIN 
 
ie_urgente_w		:= 'N';
 
select	ie_documento 
into STRICT	ie_documento_w 
from (	SELECT	'S' ie_documento 
	from	solic_compra_item 
	where	nr_seq_aprovacao = nr_seq_processo_p 
	
union
 
	SELECT	'O' ie_documento 
	from	ordem_compra_item 
	where	nr_seq_aprovacao = nr_seq_processo_p 
	
union
 
	select	'R' ie_documento 
	from	item_requisicao_material 
	where	nr_seq_aprovacao = nr_seq_processo_p) alias0;
 
if (ie_documento_w = 'O') then 
 
	select	coalesce(max(ie_urgente),'N') 
	into STRICT	ie_urgente_w 
	from	ordem_compra b, 
		ordem_compra_item a 
	where	a.nr_ordem_compra		= b.nr_ordem_compra 
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;
 
if (ie_documento_w = 'S') then 
 
	select	coalesce(max(b.ie_urgente),'N') 
	into STRICT	ie_urgente_w 
	from	solic_compra b, 
		solic_compra_item a 
	where	a.nr_solic_compra		= b.nr_solic_compra 
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;
 
if (ie_documento_w = 'R') then 
 
	select	coalesce(max(b.ie_urgente),'N') 
	into STRICT	ie_urgente_w 
	from	requisicao_material b, 
		item_requisicao_material a 
	where	a.nr_requisicao		= b.nr_requisicao 
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;
 
return	ie_urgente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
