SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proc_autorizado ( nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_autorizado_w	varchar(1):='N';


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_autorizado_w
	from   	autorizacao_convenio a,
		estagio_autorizacao b
	where  	a.nr_seq_estagio    	= b.nr_sequencia
	and	b.ie_interno	   	= 10
	and	a.ie_tipo_autorizacao	= 3
	and	a.nr_seq_agenda		= nr_seq_agenda_p;
end if;

return	ie_autorizado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_proc_autorizado ( nr_seq_agenda_p bigint) FROM PUBLIC;

