CREATE OR REPLACE FUNCTION obter_se_proc_autor_pacote (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

cd_convenio_w		bigint;
cd_estabelecimento_w	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
ds_retorno_w		varchar(255);


BEGIN 
 
select 	max(b.cd_procedimento), 
	max(b.ie_origem_proced), 
	max(a.cd_convenio), 
	max((Obter_dados_autor_convenio(a.nr_sequencia,'E'))::numeric ) 
into STRICT	cd_procedimento_w, 
	ie_origem_proced_w, 
	cd_convenio_w, 
	cd_estabelecimento_w 
from 	procedimento_autorizado b, 
	autorizacao_convenio a 
where	b.nr_sequencia_autor	= a.nr_sequencia 
and	b.nr_sequencia		= nr_sequencia_p;
 
select 	substr(Obter_se_pacote_convenio(cd_procedimento_w, ie_origem_proced_w,cd_convenio_W,cd_estabelecimento_W),1,1) 
into STRICT	ds_retorno_w
;
 
return	ds_retorno_w;
 
end;
