CREATE OR REPLACE FUNCTION obter_se_proc_cirurgico ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cirurgico_w			varchar(01);


BEGIN
ie_cirurgico_w			:= 'N';

if (coalesce(ie_origem_proced_p, 0) = 1) then
	begin
	if (substr(cd_procedimento_p,1,2) >= 39) and (substr(cd_procedimento_p,1,2) <= 56) then
		ie_cirurgico_w	:= 'S';
	end if;
	end;
end if;

if (coalesce(ie_origem_proced_p, 0) = 5) then
	begin
	if (cd_procedimento_p 		>= 30101018) and (cd_procedimento_p 		<= 31602223) then
		ie_cirurgico_w	:= 'S';
	end if;
	end;
end if;


RETURN ie_cirurgico_w;

END;
$body$
