CREATE OR REPLACE FUNCTION obter_se_proc_crit_repasse_dia (nr_seq_criterio_p bigint, ie_dia_semana_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
cont_w		bigint;


BEGIN

select	count(1)
into STRICT	cont_w
from	proc_crit_repasse_dia
where	nr_seq_criterio	= nr_seq_criterio_p  LIMIT 1;

ds_retorno_w		:= 'S';
if (cont_w > 0) then
	select	count(1)
	into STRICT	cont_w
	from	proc_crit_repasse_dia
	where	nr_seq_criterio	= nr_seq_criterio_p
	and	ie_dia_semana	= ie_dia_semana_p  LIMIT 1;

	ds_retorno_w		:= 'N';

	if (cont_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
