CREATE OR REPLACE FUNCTION obter_se_proc_dupla_chec (nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE

			
ie_retorno_w 		varchar(1) := 'N';
cd_material_w		bigint;
nr_sequencia_w		bigint;				
nr_prescricao_w		bigint;	
nr_seq_solucao_w	integer;
ie_agrupador_w		smallint;
		
C01 CURSOR FOR
	SELECT	b.nr_prescricao,
		a.nr_sequencia,
		nr_seq_solucao,
		b.ie_agrupador,
		b.cd_material
	from	prescr_material b,
		prescr_mat_hor a
	where	b.nr_sequencia		= a.nr_seq_material
	and	b.nr_prescricao		= a.nr_prescricao
	and	a.nr_seq_processo	= nr_seq_processo_p
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S'
	and 	b.ie_agrupador in (1,4,5,8,13)
	order by
		b.nr_prescricao desc,
		b.nr_sequencia,
		a.nr_sequencia;	
		

BEGIN

open C01;
loop
fetch C01 into	
	nr_prescricao_w,
	nr_sequencia_w,
	nr_seq_solucao_w,
	ie_agrupador_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_retorno_w <> 'S') then
		ie_retorno_w	:= coalesce(obter_se_medic_dupla_chec(cd_material_w, wheb_usuario_pck.get_cd_estabelecimento, nr_prescricao_w),'N');
			
		if (ie_retorno_w = 'N') then		
			select	coalesce(max(ie_dupla_checagem),'N')
			into STRICT	ie_retorno_w
			from	material
			where	cd_material = cd_material_w;
		end if;
	end if;
	end;
end loop;
close C01;		
		
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
