CREATE OR REPLACE FUNCTION obter_se_proc_duplic_prescr ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_proced_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_material_exame_p text, nr_seq_proc_interno_p bigint, ie_lado_p text) RETURNS varchar AS $body$
DECLARE


ie_proced_duplic_w	varchar(1);
dt_prev_execucao_w	timestamp;
qt_registros_w	 	bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_proced_p IS NOT NULL AND nr_seq_proced_p::text <> '') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_proced_duplic_w
	from	prescr_procedimento b,
			prescr_medica a
	where	b.nr_prescricao		= a.nr_prescricao
	and		b.cd_procedimento		= cd_procedimento_p
	and		b.ie_origem_proced		= ie_origem_proced_p
	and		coalesce(b.nr_seq_proc_interno,0)	= coalesce(nr_seq_proc_interno_p,0)
	and		coalesce(b.cd_material_exame,'0')	= coalesce(cd_material_exame_p,'0')
	and		(a.nr_prescricao = nr_prescricao_p AND b.nr_sequencia <> nr_seq_proced_p)
	and		coalesce(b.nr_seq_origem::text, '') = ''
	and		a.nr_atendimento		= nr_atendimento_p
	and		coalesce(b.dt_suspensao::text, '') = ''
	and		((coalesce(ie_lado_p::text, '') = '') or (b.ie_lado = ie_lado_p));

end if;

return ie_proced_duplic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
