CREATE OR REPLACE FUNCTION obter_se_proc_dupl_estab ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_convenio_p convenio.cd_convenio%type, cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, nr_seq_proc_interno_p proc_interno.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) := 'S';
qt_regra_w		bigint := 0;
cd_grupo_proc_w		grupo_proc.cd_grupo_proc%type;
cd_especialidade_w	especialidade_proc.cd_especialidade%type;
cd_area_proced_w	area_procedimento.cd_area_procedimento%type;

BEGIN

begin
select 	coalesce(max(cd_grupo_proc),0),
	coalesce(max(cd_especialidade),0),
	coalesce(max(cd_area_procedimento),0)
into STRICT	cd_grupo_proc_w,
	cd_especialidade_w,
	cd_area_proced_w
from	estrutura_procedimento_v
where	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
when others then
	cd_grupo_proc_w		:= 0;
	cd_especialidade_w	:= 0;
	cd_area_proced_w	:= 0;
end;

select	count(1)
into STRICT	qt_regra_w
from	regra_rest_proc_dupl_estab
where	cd_convenio 		= cd_convenio_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	ie_situacao		= 'A'
and (coalesce(cd_area_procedimento,0) = 0 or cd_area_procedimento = cd_area_proced_w)
and (coalesce(cd_especialidade,0) = 0 or cd_especialidade = cd_especialidade_w)
and (coalesce(cd_grupo_proc,0) = 0 or cd_grupo_proc = cd_grupo_proc_w)
and (coalesce(cd_procedimento,0) = 0 or cd_procedimento = cd_procedimento_p)
and (coalesce(ie_origem_proced,0) = 0 or ie_origem_proced = ie_origem_proced_p)
and (coalesce(nr_seq_proc_interno,0) = 0 or nr_seq_proc_interno = nr_seq_proc_interno_p)  LIMIT 1;

if (qt_regra_w > 0) then
	ds_retorno_w := 'N';
end if;

return	coalesce(ds_retorno_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
