CREATE OR REPLACE FUNCTION obter_se_proc_enfermagem_pa (cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ie_enfermagem_w	varchar(1) := 'N';
cd_area_proc_w	bigint;
cd_espec_proc_w	bigint;
cd_grupo_proc_w	bigint;


BEGIN
if (coalesce(cd_procedimento_p,0) > 0) and (coalesce(ie_origem_proced_p,0) > 0) then
	/* obter estrutura procedimento */

	select	max(cd_area_procedimento),
		max(cd_especialidade),
		max(cd_grupo_proc)
	into STRICT	cd_area_proc_w,
		cd_espec_proc_w,
		cd_grupo_proc_w
	from	estrutura_procedimento_v
	where	cd_procedimento	= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	/* validar enfermagem */

	begin
		select	'S'
		into STRICT	ie_enfermagem_w
		from	procedimento_enf_pa
		where	coalesce(cd_area_procedimento,cd_area_proc_w)	= cd_area_proc_w
		and	coalesce(cd_especialidade,cd_espec_proc_w)	= cd_espec_proc_w
		and	coalesce(cd_grupo_proc,cd_grupo_proc_w)		= cd_grupo_proc_w
		and	coalesce(cd_procedimento,cd_procedimento_p)	= cd_procedimento_p
		and	coalesce(ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p  LIMIT 1;
	exception
	when others then
		ie_enfermagem_w := 'N';
	end;

end if;

return ie_enfermagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
